def test_sentry_app_actor(self): result = AppPlatformEvent( resource="issue", action="assigned", install=self.install, data={}, actor=self.sentry_app.proxy_user, )
assert json.loads(result.body)["actor"] == { "type": "application", "id": self.sentry_app.uuid, "name": self.sentry_app.name, }
signature = self.sentry_app.build_signature(result.body)
assert result.headers["Content-Type"] == "application/json" assert result.headers["Sentry-Hook-Resource"] == "issue" assert result.headers["Sentry-Hook-Signature"] == signature

def test_user_actor(self): result = AppPlatformEvent( resource="installation", action="created", install=self.install, data={}, actor=self.user, )
assert json.loads(result.body)["actor"] == { "type": "user", "id": self.user.id, "name": self.user.name, }
signature = self.sentry_app.build_signature(result.body)
assert result.headers["Content-Type"] == "application/json" assert result.headers["Sentry-Hook-Resource"] == "installation" assert result.headers["Sentry-Hook-Signature"] == signature

