        "schemas": ["urn:ietf:params:scim:api:messages:2.0:ListResponse"],
        "totalResults": 1,
        "startIndex": 1,
        "itemsPerPage": 1,
        "Resources": [
            {
                "schemas": ["urn:ietf:params:scim:schemas:core:2.0:Group"],
                "id": str(team.id),
                "displayName": team.name,
                "members": [],
                "meta": {"resourceType": "Group"},
            }
        ],
    }

