def test_superuser_can_change_is_active(self): superuser = self.create_user(email="b@example.com", is_superuser=True) self.login_as(user=superuser, superuser=True)
resp = self.get_valid_response( self.user.id, isActive="false", ) assert resp.data["id"] == str(self.user.id)
user = User.objects.get(id=self.user.id) assert not user.is_active

def test_superuser_with_permission_can_add_staff(self): superuser = self.create_user(email="b@example.com", is_superuser=True) UserPermission.objects.create(user=superuser, permission="users.admin") self.login_as(user=superuser, superuser=True)
resp = self.get_valid_response( self.user.id, isStaff="true", ) assert resp.data["id"] == str(self.user.id)
user = User.objects.get(id=self.user.id) assert user.is_staff

