path = f"/api/0/issues/{self.group.id}/integrations/{integration.id}/?action=create"
with self.feature( { "organizations:integrations-issue-basic": False, "organizations:integrations-issue-sync": False, } ): response = self.client.get(path) assert response.status_code == 400 assert response.data["detail"] == "Your organization does not have access to this feature."

