def test_simple_put(self): self.login_as(user=self.user) org = self.organization group = self.create_group() integration = Integration.objects.create(provider="example", name="Example") integration.add_organization(org, self.user)
path = f"/api/0/issues/{group.id}/integrations/{integration.id}/" with self.feature("organizations:integrations-issue-basic"): response = self.client.put(path, data={"externalIssue": "APP-123"})
assert response.status_code == 201 external_issue = ExternalIssue.objects.get( key="APP-123", integration_id=integration.id, organization_id=org.id ) assert external_issue.title == "This is a test external issue title" assert external_issue.description == "This is a test external issue description" assert GroupLink.objects.filter( linked_type=GroupLink.LinkedType.issue, group_id=group.id, linked_id=external_issue.id, ).exists()
activity = Activity.objects.filter(type=Activity.CREATE_ISSUE)[0] assert activity.project_id == group.project_id assert activity.group_id == group.id assert activity.ident is None assert activity.user_id == self.user.id assert activity.data == { "title": "This is a test external issue title", "provider": "Example", "location": "https://example/issues/APP-123", "label": "display name: APP-123", }

def test_superuser_sees_unowned_published_requests(self): self.login_as(user=self.superuser, superuser=True)
buffer = SentryAppWebhookRequestsBuffer(self.unowned_published_app) buffer.add_request( response_code=200, org_id=self.org.id, event="issue.assigned", url=self.unowned_published_app.webhook_url, ) buffer.add_request( response_code=500, org_id=self.org.id, event="issue.assigned", url=self.unowned_published_app.webhook_url, )
url = reverse("sentry-api-0-sentry-app-requests", args=[self.unowned_published_app.slug]) response = self.client.get(url, format="json") assert response.status_code == 200 assert len(response.data) == 2 assert response.data[0]["organization"]["slug"] == self.org.slug assert response.data[0]["sentryAppSlug"] == self.unowned_published_app.slug assert response.data[0]["responseCode"] == 500

def test_linked_error_not_returned_if_project_doesnt_belong_to_org(self): self.login_as(user=self.user) unowned_project = self.create_project(organization=self.create_organization())
buffer = SentryAppWebhookRequestsBuffer(self.published_app) buffer.add_request( response_code=200, org_id=self.org.id, event="issue.assigned", url=self.unpublished_app.webhook_url, error_id=self.event_id, project_id=unowned_project.id, )
url = reverse("sentry-api-0-sentry-app-requests", args=[self.published_app.slug]) response = self.client.get(url, format="json") assert response.status_code == 200 assert len(response.data) == 1 assert response.data[0]["organization"]["slug"] == self.org.slug assert response.data[0]["sentryAppSlug"] == self.published_app.slug assert "errorUrl" not in response.data[0]

