def test_get_for_project_with_custom_threshold(self): ProjectTransactionThresholdOverride.objects.create( transaction="earth", project=self.project, organization=self.project.organization, threshold=400, metric=TransactionMetric.LCP.value, )
with self.feature(self.feature_name): response = self.client.get( self.url, data={ "project": [self.project.id], "transaction": self.data["transaction"], }, format="json", )
assert response.status_code == 200, response.content assert response.data["threshold"] == "400" assert response.data["metric"] == "lcp"

def test_update_project_threshold(self):
    with self.feature(self.feature_name):
        response = self.client.post(
            self.url,
            data={
                "metric": "duration",
                "threshold": "300",
            },
        )

    assert response.status_code == 201, response.content
    assert response.data["threshold"] == "300"
    assert response.data["metric"] == "duration"

    with self.feature(self.feature_name):
        response = self.client.post(
            self.url,
            data={
                "metric": "lcp",
                "threshold": "400",
            },
        )

    assert response.status_code == 200, response.content
    assert response.data["threshold"] == "400"
    assert response.data["metric"] == "lcp"

def setUp(self): self.project = self.create_project() self.euser1 = EventUser.objects.create( project_id=self.project.id, ident="1", email="foo@example.com", username="foobar", ip_address="127.0.0.1", )
self.euser2 = EventUser.objects.create( project_id=self.project.id, ident="2", email="bar@example.com", username="baz", ip_address="192.168.0.1", ) self.url = reverse( "sentry-api-0-project-users", kwargs={"organization_slug": self.organization.slug, "project_slug": self.project.slug}, )
self.login_as(user=self.user)

def test_bad_headers(self):
    project = self.create_project(name="foo")

    release = Release.objects.create(organization_id=project.organization_id, version="1")
    release.add_project(project)

    url = reverse(
        "sentry-api-0-project-release-files",
        kwargs={
            "organization_slug": project.organization.slug,
            "project_slug": project.slug,
            "version": release.version,
        },
    )

    self.login_as(user=self.user)

    response = self.client.post(
        url,
        {
            "name": "http://example.com/application.js",
            "header": "lol",
            "file": SimpleUploadedFile(
                "application.js", b"function() { }", content_type="application/javascript"
            ),
        },
        format="multipart",
    )

    assert response.status_code == 400, response.content

    response = self.client.post(
        url,
        {
            "name": "http://example.com/application.js",
            "header": "X-SourceMap: http://example.com/\r\n\ntest.map.js\n",
            "file": SimpleUploadedFile(
                "application.js", b"function() { }", content_type="application/javascript"
            ),
        },
        format="multipart",
    )

    assert response.status_code == 400, response.content

def test_invalid_name(self):
    project = self.create_project(name="foo")

    release = Release.objects.create(organization_id=project.organization_id, version="1")
    release.add_project(project)

    url = reverse(
        "sentry-api-0-project-release-files",
        kwargs={
            "organization_slug": project.organization.slug,
            "project_slug": project.slug,
            "version": release.version,
        },
    )

    self.login_as(user=self.user)

    response = self.client.post(
        url,
        {
            "name": "http://exa\tmple.com/applic\nati\ron.js\n",
            "header": "X-SourceMap: http://example.com/test.map.js",
            "file": SimpleUploadedFile(
                "application.js", b"function() { }", content_type="application/javascript"
            ),
        },
        format="multipart",
    )

    assert response.status_code == 400, response.content

