def test_simple(self): url = reverse( "sentry-api-0-organization-event-details", kwargs={ "organization_slug": self.project.organization.slug, "project_slug": self.project.slug, "event_id": "a" * 32, }, )
with self.feature("organizations:discover-basic"): response = self.client.get(url, format="json")
assert response.status_code == 200, response.content assert response.data["id"] == "a" * 32 assert response.data["projectSlug"] == self.project.slug

def test_performance_flag(self):
    url = reverse(
        "sentry-api-0-organization-event-details",
        kwargs={
            "organization_slug": self.project.organization.slug,
            "project_slug": self.project.slug,
            "event_id": "a" * 32,
        },
    )
    with self.feature(
        {"organizations:discover-basic": False, "organizations:performance-view": True}
    ):
        response = self.client.get(url, format="json")
    assert response.status_code == 200, response.content
    assert response.data["id"] == "a" * 32
    assert response.data["projectSlug"] == self.project.slug

def setUp(self):
    key, value = "foo", "bar"
    self.create_event("a", tags={key: value})

    self.url = reverse(
        "sentry-api-0-project-tagkey-values",
        kwargs={
            "organization_slug": self.organization.slug,
            "project_slug": self.project.slug,
            "key": key,
        },
    )

    self.login_as(user=self.user)

