response = self.client.post( self.url, data=json.dumps(payload), content_type="application/json", HTTP_X_GITLAB_TOKEN=WEBHOOK_TOKEN, HTTP_X_GITLAB_EVENT="Merge Request Hook", ) assert response.status_code == 204 assert 0 == PullRequest.objects.count()

def test_get(self):
    project = self.create_project()

    environment = Environment.objects.create(
        project_id=project.id, organization_id=project.organization_id, name="production"
    )
    environment.add_project(project)

    self.login_as(user=self.user)

    url = reverse(
        "sentry-api-0-project-environment-details",
        kwargs={
            "organization_slug": project.organization.slug,
            "project_slug": project.slug,
            "environment": "production",
        },
    )
    response = self.client.get(url, format="json")
    assert response.status_code == 200, response.content
    assert response.data == {
        "id": "{}".format(
            EnvironmentProject.objects.get(environment__name="production", project=project).id
        ),
        "name": "production",
        "isHidden": False,
    }

    assert (
        self.client.get(
            reverse(
                "sentry-api-0-project-environment-details",
                kwargs={
                    "organization_slug": project.organization.slug,
                    "project_slug": project.slug,
                    "environment": "invalid",
                },
            ),
            format="json",
        ).status_code
        == 404
    )

def test_put(self):
    project = self.create_project()

    environment = Environment.objects.create(
        project_id=project.id, organization_id=project.organization_id, name="production"
    )
    environment.add_project(project)

    self.login_as(user=self.user)

    url = reverse(
        "sentry-api-0-project-environment-details",
        kwargs={
            "organization_slug": project.organization.slug,
            "project_slug": project.slug,
            "environment": "production",
        },
    )
    response = self.client.put(url, {"isHidden": True}, format="json")
    assert response.status_code == 200, response.content

    assert (
        EnvironmentProject.objects.get(project=project, environment=environment).is_hidden
        is True
    )

    assert (
        self.client.put(
            reverse(
                "sentry-api-0-project-environment-details",
                kwargs={
                    "organization_slug": project.organization.slug,
                    "project_slug": project.slug,
                    "environment": "invalid",
                },
            ),
            {"isHidden": True},
            format="json",
        ).status_code
        == 404
    )

def test_too_large_chunk(self): files = [] content = b"x" * (settings.SENTRY_CHUNK_UPLOAD_BLOB_SIZE + 1) files.append(SimpleUploadedFile(sha1(content).hexdigest(), content))
response = self.client.post( self.url, data={"file": files}, HTTP_AUTHORIZATION=f"Bearer {self.token.token}", format="multipart", )
assert response.status_code == 400, response.content

def test_checksum_missmatch(self): files = [] content = b"x" * (settings.SENTRY_CHUNK_UPLOAD_BLOB_SIZE + 1) files.append(SimpleUploadedFile(b"wrong checksum", content))
response = self.client.post( self.url, data={"file": files}, HTTP_AUTHORIZATION=f"Bearer {self.token.token}", format="multipart", )
assert response.status_code == 400, response.content

def test_valid_widget(self):
    data = {
        "title": "Errors over time",
        "displayType": "line",
        "queries": [
            {"name": "errors", "conditions": "event.type:error", "fields": ["count()"]}
        ],
    }
    response = self.do_request(
        "post",
        self.url(),
        data=data,
    )
    assert response.status_code == 200, response.data

