def test_hit_cache_on_success(self, mock_query):
    mock_query.return_value = {"data": [{"browser.name": "okhttp", "client_os.name": ""}]}
    response = self.get_response(self.organization.slug, userAgents=["okhttp"])
    assert response.status_code == 200
    assert response.data == {"browserName": "okhttp", "clientOsName": ""}
    assert mock_query.call_count == 1

    response = self.get_response(self.organization.slug, userAgents=["okhttp"])
    assert response.status_code == 200
    assert response.data == {"browserName": "okhttp", "clientOsName": ""}
    assert mock_query.call_count == 1

def test_older_applies_correctly(self, now): now.return_value = datetime(2020, 8, 1, 0, 0, 0, 0, tzinfo=pytz.utc)
event = self.get_event() value = 10 data = {"comparison_type": "older", "value": str(value), "time": "hour"}
rule = self.get_rule(data=data)
event.group.first_seen = timezone.now() - timedelta(hours=3) self.assertDoesNotPass(rule, event)
event.group.first_seen = timezone.now() - timedelta(hours=11) self.assertPasses(rule, event)

def test_newer_applies_correctly(self, now): now.return_value = datetime(2020, 8, 1, 0, 0, 0, 0, tzinfo=pytz.utc)
event = self.get_event() value = 10 data = {"comparison_type": "newer", "value": str(value), "time": "hour"}
rule = self.get_rule(data=data)
event.group.first_seen = timezone.now() - timedelta(hours=3) self.assertPasses(rule, event)
event.group.first_seen = timezone.now() - timedelta(hours=11) self.assertDoesNotPass(rule, event)

