def test_latest_release(self): event = self.get_event() oldRelease = Release.objects.create( organization_id=self.organization.id, version="1", date_added=datetime(2020, 9, 1, 3, 8, 24, 880386), ) oldRelease.add_project(self.project)
newRelease = Release.objects.create( organization_id=self.organization.id, version="2", date_added=datetime(2020, 9, 2, 3, 8, 24, 880386), ) newRelease.add_project(self.project)
event.data["tags"] = (("release", newRelease.version),) rule = self.get_rule() self.assertPasses(rule, event)

def test_latest_release_no_match(self): event = self.get_event() oldRelease = Release.objects.create( organization_id=self.organization.id, version="1", date_added=datetime(2020, 9, 1, 3, 8, 24, 880386), ) oldRelease.add_project(self.project)
newRelease = Release.objects.create( organization_id=self.organization.id, version="2", date_added=datetime(2020, 9, 2, 3, 8, 24, 880386), ) newRelease.add_project(self.project)
event.data["tags"] = (("release", oldRelease.version),) rule = self.get_rule() self.assertDoesNotPass(rule, event)

def test_stacktrace_filename(self):
    """Stacktrace.filename should match frames anywhere in the stack."""

    event = self.get_event(
        exception={
            "values": [
                {
                    "type": "SyntaxError",
                    "value": "hello world",
                    "stacktrace": {
                        "frames": [
                            {"filename": "example.php", "module": "example"},
                            {"filename": "somecode.php", "module": "somecode"},
                            {"filename": "othercode.php", "module": "othercode"},
                        ]
                    },
                }
            ]
        }
    )

    # correctly matching filenames, at various locations in the stacktrace
    for value in ["example.php", "somecode.php", "othercode.php"]:
        rule = self.get_rule(
            data={"match": MatchType.EQUAL, "attribute": "stacktrace.filename", "value": value}
        )
        self.assertPasses(rule, event)

    # non-matching filename
    rule = self.get_rule(
        data={"match": MatchType.EQUAL, "attribute": "stacktrace.filename", "value": "foo.php"}
    )
    self.assertDoesNotPass(rule, event)

