querystring = urlencode({"q": 'name~"stuf"'}) responses.add( responses.GET, f"https://api.bitbucket.org/2.0/repositories/sentryuser?{querystring}", json={ "values": [ {"full_name": "sentryuser/stuff"}, {"full_name": "sentryuser/stuff-2010"}, {"full_name": "sentryuser/stuff-2011"}, {"full_name": "sentryuser/stuff-2012"}, {"full_name": "sentryuser/stuff-2013"}, {"full_name": "sentryuser/stuff-2014"}, {"full_name": "sentryuser/stuff-2015"}, {"full_name": "sentryuser/stuff-2016"}, {"full_name": "sentryuser/stuff-2016"}, {"full_name": "sentryuser/stuff-2017"}, {"full_name": "sentryuser/stuff-2018"}, {"full_name": "sentryuser/stuff-2019"}, ] }, )
installation = self.integration.get_installation(self.organization) result = installation.get_repositories("stuf") assert result == [ {"identifier": "sentryuser/stuf", "name": "sentryuser/stuf"}, {"identifier": "sentryuser/stuff", "name": "sentryuser/stuff"}, {"identifier": "sentryuser/stuff-2010", "name": "sentryuser/stuff-2010"}, {"identifier": "sentryuser/stuff-2011", "name": "sentryuser/stuff-2011"}, {"identifier": "sentryuser/stuff-2012", "name": "sentryuser/stuff-2012"}, {"identifier": "sentryuser/stuff-2013", "name": "sentryuser/stuff-2013"}, {"identifier": "sentryuser/stuff-2014", "name": "sentryuser/stuff-2014"}, {"identifier": "sentryuser/stuff-2015", "name": "sentryuser/stuff-2015"}, {"identifier": "sentryuser/stuff-2016", "name": "sentryuser/stuff-2016"}, {"identifier": "sentryuser/stuff-2017", "name": "sentryuser/stuff-2017"}, {"identifier": "sentryuser/stuff-2018", "name": "sentryuser/stuff-2018"}, {"identifier": "sentryuser/stuff-2019", "name": "sentryuser/stuff-2019"}, ]

def test_search_issues(self):
    responses.add(
        responses.GET,
        "https://api.bitbucket.org/2.0/repositories/meredithanya/apples/issues",
        json={
            "values": [
                {"id": "123", "title": "Issue Title 123"},
                {"id": "456", "title": "Issue Title 456"},
            ]
        },
    )
    resp = self.client.get(
        self.path,
        data={"field": "externalIssue", "query": "issue", "repo": "meredithanya/apples"},
    )

    assert resp.status_code == 200
    assert resp.data == [
        {"label": "#123 Issue Title 123", "value": "123"},
        {"label": "#456 Issue Title 456", "value": "456"},
    ]

def test_finds_repo_results(self):
    responses.add(
        responses.GET,
        self.base_url + "/search/repositories?q=org:test%20ex",
        json={
            "items": [
                {"name": "example", "full_name": "test/example"},
                {"name": "exhaust", "full_name": "test/exhaust"},
            ]
        },
    )
    resp = self.client.get(self.url, data={"field": "repo", "query": "ex"})

    assert resp.status_code == 200
    assert resp.data == [
        {"value": "test/example", "label": "example"},
        {"value": "test/exhaust", "label": "exhaust"},
    ]

def test_default_repo_link_fields(self):
    responses.add(
        responses.GET,
        "https://api.bitbucket.org/2.0/repositories/myaccount",
        body=b"""{
            "values": [
                {"full_name": "myaccount/repo1"},
                {"full_name": "myaccount/repo2"}
            ]
        }""",
        content_type="application/json",
    )
    self.org_integration.config = {
        "project_issue_defaults": {str(self.group.project_id): {"repo": "myaccount/repo1"}}
    }
    self.org_integration.save()
    installation = self.integration.get_installation(self.organization.id)
    fields = installation.get_link_issue_config(self.group)
    repo_field = [field for field in fields if field["name"] == "repo"][0]
    assert repo_field["default"] == "myaccount/repo1"

def test_default_repo_create_fields(self):
    responses.add(
        responses.GET,
        "https://api.bitbucket.org/2.0/repositories/myaccount",
        body=b"""{
            "values": [
                {"full_name": "myaccount/repo1"},
                {"full_name": "myaccount/repo2"}
            ]
        }""",
        content_type="application/json",
    )
    self.org_integration.config = {
        "project_issue_defaults": {str(self.group.project_id): {"repo": "myaccount/repo1"}}
    }
    self.org_integration.save()
    installation = self.integration.get_installation(self.organization.id)
    fields = installation.get_create_issue_config(self.group, self.user)
    for field in fields:
        if field["name"] == "repo":
            repo_field = field
            break
    assert repo_field["default"] == "myaccount/repo1"

def test_validate_private_key(self): responses.add( responses.POST, "https://bitbucket.example.com/plugins/servlet/oauth/request-token", status=503, )
# Start pipeline and go to setup page. self.client.get(self.setup_path)
# Submit credentials data = { "url": "https://bitbucket.example.com/", "verify_ssl": False, "consumer_key": "sentry-bot", "private_key": "hot-garbage", } resp = self.client.post(self.setup_path, data=data) assert resp.status_code == 200 self.assertContains( resp, "Private key must be a valid SSH private key encoded in a PEM format." )

