for field in ("externalIssue", "parent"): resp = self.client.get(f"{path}?field={field}&query=test") assert resp.status_code == 400 assert resp.data == { "detail": "Error Communicating with Jira (HTTP 500): unknown error" }

def test_assignee_search(self): responses.add( responses.GET, "https://jira.example.org/rest/api/2/project", json=[{"key": "HSP", "id": "10000"}], match_querystring=False, )
def responder(request): query = parse_qs(urlparse(request.url).query) assert "HSP" == query["project"][0] assert "bob" == query["username"][0] return (200, {}, EXAMPLE_USER_SEARCH_RESPONSE)
responses.add_callback( responses.GET, "https://jira.example.org/rest/api/2/user/assignable/search", callback=responder, content_type="json", match_querystring=False, ) org = self.organization self.login_as(self.user)
path = reverse("sentry-extensions-jiraserver-search", args=[org.slug, self.integration.id])
resp = self.client.get(f"{path}?project=10000&field=assignee&query=bob") assert resp.status_code == 200 assert resp.data == [{"value": "bob", "label": "Bobby - bob@example.org (bob)"}]

def test_create_subscription_forbidden(self, mock_get_scopes):
    responses.replace(
        responses.POST,
        f"https://{self.vsts_account_name.lower()}.visualstudio.com/_apis/hooks/subscriptions",
        status=403,
        json={
            "$id": 1,
            "message": "The user bob is does not have permission to access this resource",
            "typeKey": "UnauthorizedRequestException",
            "errorCode": 0,
            "eventId": 3000,
        },
    )
    state = {
        "account": {"accountName": self.vsts_account_name, "accountId": self.vsts_account_id},
        "base_url": self.vsts_base_url,
        "identity": {
            "data": {
                "access_token": self.access_token,
                "expires_in": "3600",
                "refresh_token": self.refresh_token,
                "token_type": "jwt-bearer",
            }
        },
    }

    integration = VstsIntegrationProvider()

    with pytest.raises(IntegrationProviderError) as err:
        integration.build_integration(state)
    assert "sufficient account access to create webhooks" in str(err)

def test_create_subscription_unauthorized(self, mock_get_scopes):
    responses.replace(
        responses.POST,
        f"https://{self.vsts_account_name.lower()}.visualstudio.com/_apis/hooks/subscriptions",
        status=401,
        json={
            "$id": 1,
            "message": "The user bob is not authorized to access this resource",
            "typeKey": "UnauthorizedRequestException",
            "errorCode": 0,
            "eventId": 3000,
        },
    )
    state = {
        "account": {"accountName": self.vsts_account_name, "accountId": self.vsts_account_id},
        "base_url": self.vsts_base_url,
        "identity": {
            "data": {
                "access_token": self.access_token,
                "expires_in": "3600",
                "refresh_token": self.refresh_token,
                "token_type": "jwt-bearer",
            }
        },
    }

    integration = VstsIntegrationProvider()

    with pytest.raises(IntegrationProviderError) as err:
        integration.build_integration(state)
    assert "sufficient account access to create webhooks" in str(err)

