def test_basic_flow(self):
    with self.tasks():
        self.assert_setup_flow()

    integration = Integration.objects.get(provider=self.provider.key)

    assert integration.external_id == self.account_slug
    assert integration.name == "Test App"
    assert integration.metadata["services"] == [
        {
            "integration_key": "key1",
            "name": "Super Cool Service",
            "id": "PD12345",
            "type": "service",
        }
    ]
    oi = OrganizationIntegration.objects.get(
        integration=integration, organization=self.organization
    )
    assert oi.config == {}

def test_basic_flow(self): with self.tasks(): self.assert_setup_flow()
integration = Integration.objects.get(provider=self.provider.key)
assert integration.external_id == self.installation_id assert integration.name == "Test Organization" assert integration.metadata == { "access_token": None, # The metadata doesn't get saved with the timezone "Z" character # for some reason, so just compare everything but that. "expires_at": None, "icon": "http://example.com/avatar.png", "domain_name": "github.com/Test-Organization", "account_type": "Organization", } oi = OrganizationIntegration.objects.get( integration=integration, organization=self.organization ) assert oi.config == {}

def test_get_stacktrace_link_use_default_if_version_404(self): self.assert_setup_flow() integration = Integration.objects.get(provider=self.provider.key)
repo = Repository.objects.create( organization_id=self.organization.id, name="Test-Organization/foo", url="https://github.com/Test-Organization/foo", provider="integrations:github", external_id=123, config={"name": "Test-Organization/foo"}, integration_id=integration.id, ) path = "README.md" version = "12345678" default = "master" responses.add( responses.HEAD, self.base_url + f"/repos/{repo.name}/contents/{path}?ref={version}", status=404, ) responses.add( responses.HEAD, self.base_url + f"/repos/{repo.name}/contents/{path}?ref={default}", ) installation = integration.get_installation(self.organization) result = installation.get_stacktrace_link(repo, path, default, version)
assert result == "https://github.com/Test-Organization/foo/blob/master/README.md"

def test_get_stacktrace_link_file_doesnt_exists(self): self.assert_setup_flow() integration = Integration.objects.get(provider=self.provider.key)
repo = Repository.objects.create( organization_id=self.organization.id, name="Test-Organization/foo", url="https://github.com/Test-Organization/foo", provider="integrations:github", external_id=123, config={"name": "Test-Organization/foo"}, integration_id=integration.id, ) path = "README.md" version = "master" default = "master" responses.add( responses.HEAD, self.base_url + f"/repos/{repo.name}/contents/{path}?ref={version}", status=404, ) installation = integration.get_installation(self.organization) result = installation.get_stacktrace_link(repo, path, default, version)
assert not result

def create_integration(self):
    future = datetime.now() + timedelta(hours=1)
    return Integration.objects.create(
        provider=self.provider,
        name="test",
        external_id=9999,
        metadata={
            "domain_name": "github.com/test",
            "account_type": "Organization",
            "access_token": "123456789",
            "expires_at": future.replace(microsecond=0).isoformat(),
        },
    )

def create_integration(self):
    future = datetime.now() + timedelta(hours=1)
    return Integration.objects.create(
        provider=self.provider,
        name="test",
        external_id=9999,
        metadata={
            "domain_name": "github.example.org",
            "account_type": "Organization",
            "access_token": "123456789",
            "expires_at": future.replace(microsecond=0).isoformat(),
            "installation": {
                "private_key": "some private key",
                "id": 123456,
                "verify_ssl": True,
            },
        },
    )

def test_finds_external_issue_results_with_id(self):
    responses.add(
        responses.GET,
        self.base_url + "/search/issues?q=repo:example%2025",
        json={"items": [{"number": 25, "title": "AEIOU Error"}]},
    )
    resp = self.client.get(
        self.url, data={"field": "externalIssue", "query": "25", "repo": "example"}
    )

    assert resp.status_code == 200
    assert resp.data == [{"value": 25, "label": "#25 AEIOU Error"}]

def test_finds_external_issue_results_with_iid(self):
    responses.add(
        responses.GET,
        "https://example.gitlab.com/api/v4/projects/5/issues?scope=all&iids=25",
        json=[{"iid": 25, "title": "AEIOU Error", "project_id": "5"}],
    )
    resp = self.client.get(
        self.url, data={"field": "externalIssue", "query": "25", "project": "5"}
    )

    assert resp.status_code == 200
    assert resp.data == [{"value": "5#25", "label": "(#25) AEIOU Error"}]

