def test_after_link_issue(self):
    responses.add(
        responses.POST,
        "https://example.gitlab.com/api/v4/projects/2/issues/321/notes",
        json=[],
    )
    data = {"externalIssue": "2#321", "comment": "This is not good."}
    external_issue = ExternalIssue.objects.create(
        organization_id=self.organization.id, integration_id=self.integration.id, key="2#321"
    )
    self.installation.after_link_issue(external_issue, data=data)

def test_after_link_issue_failure(self):
    responses.add(
        responses.POST,
        "https://example.gitlab.com/api/v4/projects/2/issues/321/notes",
        status=502,
    )
    data = {"externalIssue": "2#321", "comment": "This is not good."}
    external_issue = ExternalIssue.objects.create(
        organization_id=self.organization.id, integration_id=self.integration.id, key="2#321"
    )
    with self.assertRaises(IntegrationError):
        self.installation.after_link_issue(external_issue, data=data)

def test_search_issues_rate_limit(self):
    responses.add(
        responses.GET,
        self.base_url + "/search/issues?q=repo:example%20ex",
        status=403,
        json={
            "message": "API rate limit exceeded",
            "documentation_url": "https://developer.github.com/v3/#rate-limiting",
        },
    )
    resp = self.client.get(
        self.url, data={"field": "externalIssue", "query": "ex", "repo": "example"}
    )
    assert resp.status_code == 429

def test_search_project_rate_limit(self):
    responses.add(
        responses.GET,
        self.base_url + "/search/repositories?q=org:test%20ex",
        status=403,
        json={
            "message": "API rate limit exceeded",
            "documentation_url": "https://developer.github.com/v3/#rate-limiting",
        },
    )
    resp = self.client.get(self.url, data={"field": "repo", "query": "ex"})
    assert resp.status_code == 429

# Request Validations

def test_create_issue(self, mock_get_jwt): responses.add( responses.POST, "https://api.github.com/app/installations/github_external_id/access_tokens", json={"token": "token_1", "expires_at": "2018-10-11T22:14:10Z"}, )
responses.add( responses.POST, "https://api.github.com/repos/getsentry/sentry/issues", json={ "number": 321, "title": "hello", "body": "This is the description", "html_url": "https://github.com/getsentry/sentry/issues/231", }, )
form_data = { "repo": "getsentry/sentry", "title": "hello", "description": "This is the description", }
assert self.integration.create_issue(form_data) == { "key": 321, "description": "This is the description", "title": "hello", "url": "https://github.com/getsentry/sentry/issues/231", "repo": "getsentry/sentry", } request = responses.calls[0].request assert request.headers["Authorization"] == "Bearer jwt_token_1"
request = responses.calls[1].request assert request.headers["Authorization"] == "token token_1" payload = json.loads(request.body) assert payload == {"body": "This is the description", "assignee": None, "title": "hello"}

def test_create_issue(self, mock_get_jwt): responses.add( responses.POST, "https://35.232.149.196/api/v3/app/installations/installation_id/access_tokens", json={"token": "token_1", "expires_at": "2018-10-11T22:14:10Z"}, )
responses.add( responses.POST, "https://35.232.149.196/api/v3/repos/getsentry/sentry/issues", json={ "number": 321, "title": "hello", "body": "This is the description", "html_url": "https://35.232.149.196/getsentry/sentry/issues/231", }, )
form_data = { "repo": "getsentry/sentry", "title": "hello", "description": "This is the description", }
assert self.integration.create_issue(form_data) == { "key": 321, "description": "This is the description", "title": "hello", "url": "https://35.232.149.196/getsentry/sentry/issues/231", "repo": "getsentry/sentry", } request = responses.calls[0].request assert request.headers["Authorization"] == "Bearer jwt_token_1"
request = responses.calls[1].request assert request.headers["Authorization"] == "token token_1" payload = json.loads(request.body) assert payload == {"body": "This is the description", "assignee": None, "title": "hello"}

def test_link_issue(self, mock_get_jwt): issue_id = 321 responses.add( responses.POST, "https://35.232.149.196/api/v3/app/installations/installation_id/access_tokens", json={"token": "token_1", "expires_at": "2018-10-11T22:14:10Z"}, )
responses.add( responses.GET, "https://35.232.149.196/api/v3/repos/getsentry/sentry/issues/321", json={ "number": issue_id, "title": "hello", "body": "This is the description", "html_url": "https://35.232.149.196/getsentry/sentry/issues/231", }, )
data = {"repo": "getsentry/sentry", "externalIssue": issue_id, "comment": "hello"}
assert self.integration.get_issue(issue_id, data=data) == { "key": issue_id, "description": "This is the description", "title": "hello", "url": "https://35.232.149.196/getsentry/sentry/issues/231", "repo": "getsentry/sentry", } request = responses.calls[0].request assert request.headers["Authorization"] == "Bearer jwt_token_1"
request = responses.calls[1].request assert request.headers["Authorization"] == "token token_1"

