    )

def test_push_event_multiple_organizations_one_missing_repo(self): # Create a repo on the primary organization repo = self.create_repo("getsentry/sentry")
# Second org with no repo. other_org = self.create_organization(owner=self.user) self.integration.add_organization(other_org, self.user)
response = self.client.post( self.url, data=PUSH_EVENT, content_type="application/json", HTTP_X_GITLAB_TOKEN=WEBHOOK_TOKEN, HTTP_X_GITLAB_EVENT="Push Hook", ) assert response.status_code == 204 commits = Commit.objects.all() assert len(commits) == 2 for commit in commits: assert commit.organization_id == self.organization.id assert commit.repository_id == repo.id

def test_prefills_data(self):
    webhook_data = json.loads(
        self.load_fixture("cloudflare/preview-webhook-authenticated.json")
    )
    webhook_data["install"]["options"]["organization"] = str(self.org.id)
    resp = self.post_webhook(data=webhook_data)

    assert resp.status_code == 200, resp.content
    assert resp.data["proceed"]
    assert resp.data["install"]["schema"]["properties"]["organization"]["enum"] == [
        str(self.org.id)
    ]
    assert resp.data["install"]["schema"]["properties"]["organization"]["enumNames"] == {
        str(self.org.id): self.org.slug
    }
    assert resp.data["install"]["options"]["organization"] == str(self.org.id)
    assert resp.data["install"]["schema"]["properties"]["project"]["enum"] == [
        str(self.project.id)
    ]
    assert resp.data["install"]["schema"]["properties"]["project"]["enumNames"] == {
        str(self.project.id): self.project.slug
    }
    assert resp.data["install"]["options"]["project"] == str(self.project.id)
    assert resp.data["install"]["schema"]["properties"]["dsn"]["enum"] == [
        self.key.get_dsn(public=True)
    ]
    assert resp.data["install"]["options"]["dsn"] == str(self.key.get_dsn(public=True))

def test_multiple_projects(self): project2 = self.create_project(name="b", teams=[self.team])
webhook_data = json.loads( self.load_fixture("cloudflare/preview-webhook-authenticated.json") ) webhook_data["install"]["options"]["organization"] = str(self.org.id) resp = self.post_webhook(webhook_data)
