def test_link_issue(self): responses.add( responses.GET, "https://api.github.com/repos/getsentry/sentry/issues/1", json={"number": 1, "title": "Hello world"}, ) responses.add( responses.POST, "https://api.github.com/repos/getsentry/sentry/issues/1/comments", json={"body": "Hello"}, )
self.plugin.set_option("repo", "getsentry/sentry", self.project) group = self.create_group(message="Hello world", culprit="foo.bar")
request = self.request.get("/") request.user = AnonymousUser() form_data = {"comment": "Hello", "issue_id": "1"} with self.assertRaises(PluginError): self.plugin.link_issue(request, group, form_data)
request.user = self.user self.login_as(self.user) UserSocialAuth.objects.create( user=self.user, provider=self.plugin.auth_provider, extra_data={"access_token": "foo"} )
assert self.plugin.link_issue(request, group, form_data) == {"title": "Hello world"} request = responses.calls[-1].request assert request.headers["Authorization"] == "Bearer foo" payload = json.loads(request.body) assert payload == {"body": "Hello"}

def test_create_issue(self): responses.add( responses.POST, "https://api.github.com/repos/getsentry/sentry/issues", json={"number": 1, "title": "Hello world"}, )
self.plugin.set_option("repo", "getsentry/sentry", self.project) group = self.create_group(message="Hello world", culprit="foo.bar")
request = self.request.get("/") request.user = AnonymousUser() form_data = {"title": "Hello", "description": "Fix this."} with self.assertRaises(PluginError): self.plugin.create_issue(request, group, form_data)
