assert self.plugin.create_issue(request, group, form_data) == 1 request = responses.calls[0].request assert request.headers["Authorization"] == "Bearer foo" payload = json.loads(request.body) assert payload == {"title": "Hello", "body": "Fix this.", "assignee": None}

