def test_reraise_error(self): responses.add( responses.POST, "https://splunk.example.com:8088/services/collector", status=500 )
self.plugin.set_option("token", "12345678-1234-1234-1234-1234567890AB", self.project) self.plugin.set_option("index", "main", self.project) self.plugin.set_option("instance", "https://splunk.example.com:8088", self.project)
event = self.store_event( data={"message": "Hello world", "level": "warning"}, project_id=self.project.id ) with self.options({"system.url-prefix": "http://example.com"}): with self.assertRaises(ApiError): self.plugin.post_process(event)

def test_http_payload(self):
    event = self.store_event(
        data={
            "request": {
                "url": "http://example.com",
                "method": "POST",
                "headers": {"Referer": "http://example.com/foo"},
            }
        },
        project_id=self.project.id,
    )

    result = self.plugin.get_event_payload(event)
    assert result["event"]["request_url"] == "http://example.com/"
    assert result["event"]["request_method"] == "POST"
    assert result["event"]["request_referer"] == "http://example.com/foo"

def test_error_payload(self):
    event = self.store_event(
        data={
            "exception": {"values": [{"type": "ValueError", "value": "foo bar"}]},
            "type": "error",
        },
        project_id=self.project.id,
    )

    result = self.plugin.get_event_payload(event)
    assert result["event"]["type"] == "error"
    assert result["event"]["exception_type"] == "ValueError"
    assert result["event"]["exception_value"] == "foo bar"

def test_simple_notification(self): responses.add("POST", "https://api.pushover.net/1/messages.json", body=SUCCESS) self.plugin.set_option("userkey", "abcdef", self.project) self.plugin.set_option("apikey", "ghijkl", self.project)
event = self.store_event( data={"message": "Hello world", "level": "warning"}, project_id=self.project.id ) group = event.group
rule = Rule.objects.create(project=self.project, label="my rule")
notification = Notification(event=event, rule=rule)
with self.options({"system.url-prefix": "http://example.com"}): self.plugin.notify(notification)
request = responses.calls[0].request payload = parse_qs(request.body) assert payload == { "message": [f"{event.title}\n\nTags: level=warning"], "title": ["Bar: Hello world"], "url": [ f"http://example.com/organizations/baz/issues/{group.id}/?referrer=pushover_plugin" ], "url_title": ["Issue Details"], "priority": ["0"], "user": ["abcdef"], "token": ["ghijkl"], "expire": ["90"], "retry": ["30"], }

def test_view_autocomplete(self): responses.add( responses.GET, "https://api.trello.com/1/search", json={ "cards": [ {"id": "4fsdafad", "name": "KeyError", "idShort": 1, "shortLink": "0lr"}, {"id": "f4usdfa", "name": "Key Missing", "idShort": 3, "shortLink": "9lf"}, ] }, )
request = self.make_request(user=self.user, method="GET") request.GET["autocomplete_field"] = "issue_id" request.GET["autocomplete_query"] = "Key"
response = self.plugin.view_autocomplete(request, self.group) assert response.data == { "issue_id": [ {"id": "0lr", "text": "(#1) KeyError"}, {"id": "9lf", "text": "(#3) Key Missing"}, ] }
request = responses.calls[0].request url = urlparse(request.url) query = dict(parse_qsl(url.query))
assert url.path == "/1/search" assert query == { "cards_limit": "100", "partial": "true", "modelTypes": "cards", "token": "7c8951d1", "card_fields": "name,shortLink,idShort", "key": "39g", "query": "Key", "idOrganizations": "f187", }

