        "start": "2021-03-14T00:00:00Z",
        "end": "2021-03-14T12:28:00Z",
        "intervals": ["2021-03-14T00:00:00Z"],
        "groups": [
            {"by": {}, "series": {"sum(quantity)": [7]}, "totals": {"sum(quantity)": 7}}
        ],
    }

