def test_tag_caching(self, mock_snuba_query): user = self.create_user() org = self.create_organization() team = self.create_team(organization=org) self.create_member(organization=org, user=user, teams=[team]) self.create_project(organization=org, teams=[team]) self.login_as(user=user)
with self.options({"snuba.tagstore.cache-tagkeys-rate": 1.0}): url = reverse("sentry-api-0-organization-tags", kwargs={"organization_slug": org.slug}) response = self.client.get(url, {"use_cache": "1", "statsPeriod": "14d"}, format="json") assert response.status_code == 200, response.content assert mock_snuba_query.call_count == 1
response = self.client.get(url, {"use_cache": "1", "statsPeriod": "14d"}, format="json") assert response.status_code == 200, response.content # Cause we're caching, we shouldn't call snuba again assert mock_snuba_query.call_count == 1

def test_different_statsperiod_caching(self, mock_snuba_query): user = self.create_user() org = self.create_organization() team = self.create_team(organization=org) self.create_member(organization=org, user=user, teams=[team]) self.create_project(organization=org, teams=[team]) self.login_as(user=user)
with self.options({"snuba.tagstore.cache-tagkeys-rate": 1.0}): url = reverse("sentry-api-0-organization-tags", kwargs={"organization_slug": org.slug}) response = self.client.get(url, {"use_cache": "1", "statsPeriod": "14d"}, format="json") assert response.status_code == 200, response.content # Empty cache, we should query snuba assert mock_snuba_query.call_count == 1
response = self.client.get(url, {"use_cache": "1", "statsPeriod": "30d"}, format="json") assert response.status_code == 200, response.content # With a different statsPeriod, we shouldn't use cache and still query snuba assert mock_snuba_query.call_count == 2

def test_simple(self):
    response = self.do_request(
        {
            "start": iso_format(self.day_ago),
            "end": iso_format(self.day_ago + timedelta(hours=2)),
            "interval": "1h",
        },
    )

    assert response.status_code == 200, response.content
    assert [attrs for time, attrs in response.data["data"]] == [[{"count": 1}], [{"count": 2}]]

