def test_simple(self):
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"number": "one"},
        },
        project_id=self.project2.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"number": "one"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"number": "two"},
        },
        project_id=self.project.id,
    )

    with self.feature(self.features):
        response = self.client.get(self.url, format="json")

    assert response.status_code == 200, response.content
    expected = [
        {"count": 2, "name": "one", "value": "one"},
        {"count": 1, "name": "two", "value": "two"},
    ]
    self.assert_facet(response, "number", expected)

def test_with_condition(self):
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "how to make fast",
            "tags": {"color": "green"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Delet the Data",
            "tags": {"color": "red"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Data the Delet ",
            "tags": {"color": "yellow"},
        },
        project_id=self.project2.id,
    )

    with self.feature(self.features):
        response = self.client.get(self.url, {"query": "color:yellow"}, format="json")

    assert response.status_code == 200, response.content
    expected = [{"count": 1, "name": "yellow", "value": "yellow"}]
    self.assert_facet(response, "color", expected)

def test_with_message_query(self):
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "how to make fast",
            "tags": {"color": "green"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Delet the Data",
            "tags": {"color": "red"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Data the Delet ",
            "tags": {"color": "yellow"},
        },
        project_id=self.project2.id,
    )

    with self.feature(self.features):
        response = self.client.get(self.url, {"query": "delet"}, format="json")

    assert response.status_code == 200, response.content
    expected = [
        {"count": 1, "name": "yellow", "value": "yellow"},
        {"count": 1, "name": "red", "value": "red"},
    ]
    self.assert_facet(response, "color", expected)

def test_project_key(self):
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"color": "green"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"number": "one"},
        },
        project_id=self.project2.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"color": "green"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={"event_id": uuid4().hex, "timestamp": self.min_ago_iso, "tags": {"color": "red"}},
        project_id=self.project.id,
    )

    with self.feature(self.features):
        response = self.client.get(self.url, format="json")

    assert response.status_code == 200, response.content
    expected = [
        {"count": 3, "name": self.project.slug, "value": self.project.id},
        {"count": 1, "name": self.project2.slug, "value": self.project2.id},
    ]
    self.assert_facet(response, "project", expected)

def test_with_conditional_filter(self):
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "how to make fast",
            "tags": {"color": "green"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Delet the Data",
            "tags": {"color": "red"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "message": "Data the Delet ",
            "tags": {"color": "yellow"},
        },
        project_id=self.project2.id,
    )

    with self.feature(self.features):
        response = self.client.get(
            self.url, {"query": "color:yellow OR color:red"}, format="json"
        )

    assert response.status_code == 200, response.content
    expected = [
        {"count": 1, "name": "yellow", "value": "yellow"},
        {"count": 1, "name": "red", "value": "red"},
    ]
    self.assert_facet(response, "color", expected)

