            "timestamp": self.min_ago_iso,
            "tags": {"number": "two"},
        },
        project_id=self.project.id,
    )
    self.store_event(
        data={
            "event_id": uuid4().hex,
            "timestamp": self.min_ago_iso,
            "tags": {"number": "one"},
