    self.assert_facet(response, "number", expected)

def test_comparison(self): with freeze_time(before_now(minutes=0)): self._make_sessions(10, 1) # Create sessions for previous period self._make_sessions(10, 60 * 24 + 20)
# Test data is 2 events in the current period and 1 events in the comparison period. # Number of sessions is 20 in each period, so current period is 20% of sessions, prev # is 10%. Overall a 100% increase comparitively. event = self.store_event( data={ "fingerprint": ["something_random"], "timestamp": iso_format(before_now(minutes=1)), }, project_id=self.project.id, ) self.increment( event, 1, timestamp=now() - timedelta(minutes=1), ) self.increment( event, 1, timestamp=now() - timedelta(days=1, minutes=20), ) data = { "interval": "1h", "value": 99, "comparisonType": "percent", "comparisonInterval": "1d", } rule = self.get_rule(data=data, rule=Rule(environment_id=None)) self.assertPasses(rule, event)
data = { "interval": "1h", "value": 101, "comparisonType": "percent", "comparisonInterval": "1d", } rule = self.get_rule(data=data, rule=Rule(environment_id=None)) self.assertDoesNotPass(rule, event)

def increment(self, event, count, environment=None, timestamp=None):
    data = {
        "fingerprint": event.data["fingerprint"],
        "timestamp": iso_format(timestamp) if timestamp else iso_format(before_now(minutes=1)),
    }
    if environment:
        data["environment"] = environment

    for _ in range(count):
        self.store_event(
            data=data,
            project_id=self.project.id,
        )

def increment(self, event, count, environment=None, timestamp=None):
    data = {
        "fingerprint": event.data["fingerprint"],
        "timestamp": iso_format(timestamp) if timestamp else iso_format(before_now(minutes=1)),
    }
    if environment:
        data["environment"] = environment

    for _ in range(count):
        event_data = deepcopy(data)
        event_data["user"] = {"id": uuid4().hex}
        self.store_event(
            data=event_data,
            project_id=self.project.id,
        )

