def img_tensorize(im, input_format="RGB"):
    assert isinstance(im, str)
    if os.path.isfile(im):
        img = cv2.imread(im)
    else:
        img = get_image_from_url(im)
        assert img is not None, f"could not connect to: {im}"
    img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)
    if input_format == "RGB":
        img = img[:, :, ::-1]
    return img

def img_tensorize(im, input_format="RGB"):
    assert isinstance(im, str)
    if os.path.isfile(im):
        img = cv2.imread(im)
    else:
        img = get_image_from_url(im)
        assert img is not None, f"could not connect to: {im}"
    img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)
    if input_format == "RGB":
        img = img[:, :, ::-1]
    return img

def draw_boxes(self, boxes, obj_ids=None, obj_scores=None, attr_ids=None, attr_scores=None):
    if len(boxes.shape) > 2:
        boxes = boxes[0]
    if len(obj_ids.shape) > 1:
        obj_ids = obj_ids[0]
    if len(obj_scores.shape) > 1:
        obj_scores = obj_scores[0]
    if len(attr_ids.shape) > 1:
        attr_ids = attr_ids[0]
    if len(attr_scores.shape) > 1:
        attr_scores = attr_scores[0]
    if isinstance(boxes, torch.Tensor):
        boxes = boxes.numpy()
    if isinstance(boxes, list):
        boxes = np.array(boxes)
    assert isinstance(boxes, np.ndarray)
    areas = np.prod(boxes[:, 2:] - boxes[:, :2], axis=1)
    sorted_idxs = np.argsort(-areas).tolist()
    boxes = boxes[sorted_idxs] if boxes is not None else None
    obj_ids = obj_ids[sorted_idxs] if obj_ids is not None else None
    obj_scores = obj_scores[sorted_idxs] if obj_scores is not None else None
    attr_ids = attr_ids[sorted_idxs] if attr_ids is not None else None
    attr_scores = attr_scores[sorted_idxs] if attr_scores is not None else None

    assigned_colors = [self._random_color(maximum=1) for _ in range(len(boxes))]
    assigned_colors = [assigned_colors[idx] for idx in sorted_idxs]
    if obj_ids is not None:
        labels = self._create_text_labels_attr(obj_ids, obj_scores, attr_ids, attr_scores)
        for i in range(len(boxes)):
            color = assigned_colors[i]
            self.add_box(boxes[i], color)
            self.draw_labels(labels[i], boxes[i], color)

def draw_boxes(self, boxes, obj_ids=None, obj_scores=None, attr_ids=None, attr_scores=None):
    if len(boxes.shape) > 2:
        boxes = boxes[0]
    if len(obj_ids.shape) > 1:
        obj_ids = obj_ids[0]
    if len(obj_scores.shape) > 1:
        obj_scores = obj_scores[0]
    if len(attr_ids.shape) > 1:
        attr_ids = attr_ids[0]
    if len(attr_scores.shape) > 1:
        attr_scores = attr_scores[0]
    if isinstance(boxes, torch.Tensor):
        boxes = boxes.numpy()
    if isinstance(boxes, list):
        boxes = np.array(boxes)
    assert isinstance(boxes, np.ndarray)
    areas = np.prod(boxes[:, 2:] - boxes[:, :2], axis=1)
    sorted_idxs = np.argsort(-areas).tolist()
    boxes = boxes[sorted_idxs] if boxes is not None else None
    obj_ids = obj_ids[sorted_idxs] if obj_ids is not None else None
    obj_scores = obj_scores[sorted_idxs] if obj_scores is not None else None
    attr_ids = attr_ids[sorted_idxs] if attr_ids is not None else None
    attr_scores = attr_scores[sorted_idxs] if attr_scores is not None else None

    assigned_colors = [self._random_color(maximum=1) for _ in range(len(boxes))]
    assigned_colors = [assigned_colors[idx] for idx in sorted_idxs]
    if obj_ids is not None:
        labels = self._create_text_labels_attr(obj_ids, obj_scores, attr_ids, attr_scores)
        for i in range(len(boxes)):
            color = assigned_colors[i]
            self.add_box(boxes[i], color)
            self.draw_labels(labels[i], boxes[i], color)

def draw_labels(self, label, box, color): x0, y0, x1, y1 = box text_pos = (x0, y0) instance_area = (y1 - y0) * (x1 - x0) small = _SMALL_OBJ * self.scale if instance_area < small or y1 - y0 < 40 * self.scale: if y1 >= self.height - 5: text_pos = (x1, y0) else: text_pos = (x0, y1)
height_ratio = (y1 - y0) / np.sqrt(self.height * self.width) lighter_color = self._change_color_brightness(color, brightness_factor=0.7) font_size = np.clip((height_ratio - 0.02) / 0.08 + 1, 1.2, 2) font_size *= 0.75 * self.font_size
self.draw_text( text=label, position=text_pos, color=lighter_color, )

def draw_labels(self, label, box, color): x0, y0, x1, y1 = box text_pos = (x0, y0) instance_area = (y1 - y0) * (x1 - x0) small = _SMALL_OBJ * self.scale if instance_area < small or y1 - y0 < 40 * self.scale: if y1 >= self.height - 5: text_pos = (x1, y0) else: text_pos = (x0, y1)
height_ratio = (y1 - y0) / np.sqrt(self.height * self.width) lighter_color = self._change_color_brightness(color, brightness_factor=0.7) font_size = np.clip((height_ratio - 0.02) / 0.08 + 1, 1.2, 2) font_size *= 0.75 * self.font_size
self.draw_text( text=label, position=text_pos, color=lighter_color, )

def save(self, saveas=None):
    if saveas is None:
        saveas = self.saveas
    if saveas.lower().endswith(".jpg") or saveas.lower().endswith(".png"):
        cv2.imwrite(
            saveas,
            self._get_buffer()[:, :, ::-1],
        )
    else:
        self.fig.savefig(saveas)

def save(self, saveas=None):
    if saveas is None:
        saveas = self.saveas
    if saveas.lower().endswith(".jpg") or saveas.lower().endswith(".png"):
        cv2.imwrite(
            saveas,
            self._get_buffer()[:, :, ::-1],
        )
    else:
        self.fig.savefig(saveas)

def _get_buffer(self):
    if not self.pynb:
        s, (width, height) = self.canvas.print_to_buffer()
        if (width, height) != (self.width, self.height):
            img = cv2.resize(self.img, (width, height))
        else:
            img = self.img
    else:
        buf = io.BytesIO()  # works for cairo backend
        self.canvas.print_rgba(buf)
        width, height = self.width, self.height
        s = buf.getvalue()
        img = self.img

    buffer = np.frombuffer(s, dtype="uint8")
    img_rgba = buffer.reshape(height, width, 4)
    rgb, alpha = np.split(img_rgba, [3], axis=2)

    try:
        import numexpr as ne  # fuse them with numexpr

        visualized_image = ne.evaluate("img * (1 - alpha / 255.0) + rgb * (alpha / 255.0)")
    except ImportError:
        alpha = alpha.astype("float32") / 255.0
        visualized_image = img * (1 - alpha) + rgb * alpha

    return visualized_image.astype("uint8")

def _get_buffer(self):
    if not self.pynb:
        s, (width, height) = self.canvas.print_to_buffer()
        if (width, height) != (self.width, self.height):
            img = cv2.resize(self.img, (width, height))
        else:
            img = self.img
    else:
        buf = io.BytesIO()  # works for cairo backend
        self.canvas.print_rgba(buf)
        width, height = self.width, self.height
        s = buf.getvalue()
        img = self.img

    buffer = np.frombuffer(s, dtype="uint8")
    img_rgba = buffer.reshape(height, width, 4)
    rgb, alpha = np.split(img_rgba, [3], axis=2)

    try:
        import numexpr as ne  # fuse them with numexpr

        visualized_image = ne.evaluate("img * (1 - alpha / 255.0) + rgb * (alpha / 255.0)")
    except ImportError:
        alpha = alpha.astype("float32") / 255.0
        visualized_image = img * (1 - alpha) + rgb * alpha

    return visualized_image.astype("uint8")

def __call__(self, imgs):
    img_augs = []
    for img in imgs:
        h, w = img.shape[:2]
        # later: provide list and randomly choose index for resize
        size = np.random.randint(self.short_edge_length[0], self.short_edge_length[1] + 1)
        if size == 0:
            return img
        scale = size * 1.0 / min(h, w)
        if h < w:
            newh, neww = size, scale * w
        else:
            newh, neww = scale * h, size
        if max(newh, neww) > self.max_size:
            scale = self.max_size * 1.0 / max(newh, neww)
            newh = newh * scale
            neww = neww * scale
        neww = int(neww + 0.5)
        newh = int(newh + 0.5)

        if img.dtype == np.uint8:
            pil_image = Image.fromarray(img)
            pil_image = pil_image.resize((neww, newh), Image.BILINEAR)
            img = np.asarray(pil_image)
        else:
            img = img.permute(2, 0, 1).unsqueeze(0)  # 3, 0, 1)  # hw(c) -> nchw
            img = nn.functional.interpolate(
                img, (newh, neww), mode=self.interp_method, align_corners=False
            ).squeeze(0)
        img_augs.append(img)

    return img_augs

def __call__(self, imgs):
    img_augs = []
    for img in imgs:
        h, w = img.shape[:2]
        # later: provide list and randomly choose index for resize
        size = np.random.randint(self.short_edge_length[0], self.short_edge_length[1] + 1)
        if size == 0:
            return img
        scale = size * 1.0 / min(h, w)
        if h < w:
            newh, neww = size, scale * w
        else:
            newh, neww = scale * h, size
        if max(newh, neww) > self.max_size:
            scale = self.max_size * 1.0 / max(newh, neww)
            newh = newh * scale
            neww = neww * scale
        neww = int(neww + 0.5)
        newh = int(newh + 0.5)

        if img.dtype == np.uint8:
            pil_image = Image.fromarray(img)
            pil_image = pil_image.resize((neww, newh), Image.BILINEAR)
            img = np.asarray(pil_image)
        else:
            img = img.permute(2, 0, 1).unsqueeze(0)  # 3, 0, 1)  # hw(c) -> nchw
            img = nn.functional.interpolate(
                img, (newh, neww), mode=self.interp_method, align_corners=False
            ).squeeze(0)
        img_augs.append(img)

    return img_augs

def pad(self, images):
    max_size = tuple(max(s) for s in zip(*[img.shape for img in images]))
    image_sizes = [im.shape[-2:] for im in images]
    images = [
        nn.functional.pad(
            im,
            [0, max_size[-1] - size[1], 0, max_size[-2] - size[0]],
            value=self.pad_value,
        )
        for size, im in zip(image_sizes, images)
    ]

    return torch.stack(images), torch.tensor(image_sizes)

def pad(self, images):
    max_size = tuple(max(s) for s in zip(*[img.shape for img in images]))
    image_sizes = [im.shape[-2:] for im in images]
    images = [
        nn.functional.pad(
            im,
            [0, max_size[-1] - size[1], 0, max_size[-2] - size[0]],
            value=self.pad_value,
        )
        for size, im in zip(image_sizes, images)
    ]

    return torch.stack(images), torch.tensor(image_sizes)

def file_generator(self):
    batch = []
    for i, file in enumerate(os.listdir(self.inputdir)):
        if self.subset_list is not None and i not in self.subset_list:
            continue
        batch.append(self._vqa_file_split(file))
        if len(batch) == self.batch:
            temp = batch
            batch = []
            yield list(map(list, zip(*temp)))

    for i in range(1):
        yield list(map(list, zip(*batch)))

def file_generator(self):
    batch = []
    for i, file in enumerate(os.listdir(self.inputdir)):
        if self.subset_list is not None and i not in self.subset_list:
            continue
        batch.append(self._vqa_file_split(file))
        if len(batch) == self.batch:
            temp = batch
            batch = []
            yield list(map(list, zip(*temp)))

    for i in range(1):
        yield list(map(list, zip(*batch)))

def tryload(stream):
    try:
        data = json.load(stream)
        try:
            data = list(data.keys())
        except Exception:
            data = [d["img_id"] for d in data]
    except Exception:
        try:
            data = eval(stream.read())
        except Exception:
            data = stream.read().split("\n")
    return data

