def endElement(self, name, value, connection):
    if name == self.element_name:
        if len(self) > 0:
            empty = []
            for e in self:
                if isinstance(e, Element):
                    if len(e) == 0:
                        empty.append(e)
            for e in empty:
                self.remove(e)
    else:
        setattr(self, self.get_name(name), value)

