def test_fluid_zcommand(self) -> None: self.login("hamlet") user = self.example_user("hamlet") user.fluid_layout_width = False user.save()
payload = dict(command="/fluid-width") result = self.client_post("/json/zcommand", payload) self.assert_json_success(result) self.assert_in_response("Changed to fluid-width mode!", result)
result = self.client_post("/json/zcommand", payload) self.assert_json_success(result) self.assert_in_response("You are still in fluid width mode", result)

